// Mobile menu toggle
document.addEventListener('DOMContentLoaded', function() {
  const mobileMenuBtn = document.querySelector('.mobile-menu-btn');
  const navLinks = document.querySelector('.nav-links');

  mobileMenuBtn.addEventListener('click', function() {
    navLinks.classList.toggle('active');
    this.setAttribute('aria-expanded', navLinks.classList.contains('active'));
  });

  // Filter buttons functionality
  const filterButtons = document.querySelectorAll('.filter-btn');
  const hackathonCards = document.querySelectorAll('.hackathon-card');

  filterButtons.forEach(button => {
    button.addEventListener('click', function() {
      const filter = this.getAttribute('data-filter');
      
      // Update active button
      filterButtons.forEach(btn => btn.classList.remove('active'));
      this.classList.add('active');

      // Filter cards
      hackathonCards.forEach(card => {
        if (filter === 'all' || card.classList.contains(filter)) {
          card.style.display = 'block';
          setTimeout(() => {
            card.style.opacity = '1';
            card.style.transform = 'translateY(0)';
          }, 10);
        } else {
          card.style.opacity = '0';
          card.style.transform = 'translateY(20px)';
          setTimeout(() => {
            card.style.display = 'none';
          }, 300);
        }
      });
    });
  });

  // Participate button functionality
  const participateButtons = document.querySelectorAll('.btn-participate');
  
  participateButtons.forEach(button => {
    button.addEventListener('click', function() {
      const card = this.closest('.hackathon-card');
      const title = card.querySelector('.hackathon-title').textContent;
      const fee = card.querySelector('.detail-value.fee').textContent;
      
      alert(`ðŸŽ‰ You're registering for: ${title}\n\nParticipation Fee: ${fee}\n\nYou'll be redirected to the registration form.`);
      
      // In a real app, this would redirect to registration page
      // window.location.href = `/register?hackathon=${encodeURIComponent(title)}`;
    });
  });

  // Close mobile menu when clicking outside
  document.addEventListener('click', function(event) {
    if (!event.target.closest('.nav-pill') && navLinks.classList.contains('active')) {
      navLinks.classList.remove('active');
      mobileMenuBtn.setAttribute('aria-expanded', 'false');
    }
  });

  // Close mobile menu on escape key
  document.addEventListener('keydown', function(event) {
    if (event.key === 'Escape' && navLinks.classList.contains('active')) {
      navLinks.classList.remove('active');
      mobileMenuBtn.setAttribute('aria-expanded', 'false');
    }
  });
});